        if (!$ogDesc && !RhdHandler::isEnglish()) {
            $ogDesc = 'Группа «РусГидро» - один из крупнейших российских энергетических холдингов. РусГидро является лидером в производстве энергии на базе возобновляемых источников, развивающей генерацию на основе энергии водных потоков, морских приливов, ветра и геотермальной энергии.';
        }

        $content =
            str_replace(
                array(
                    '[OG_DESC]',
                    '[OG_IMG]',
                ),
                array(
                    $ogDesc,
                    RhdHandler::$ogImage ?: 'http://www.rushydro.ru'.SITE_TEMPLATE_PATH.'/i/logo-FB.png',
                ),
                $content
            );
    }

    AddEventHandler('main', 'OnEndBufferContent', 'insertOGData');
} catch (RhdNotFoundException $e) {
    echo '<!--'.$e->getMessage().'-->';
    include $_SERVER['DOCUMENT_ROOT'].'/404.php';
    die();
}

unset($_GET['type']);
unset($_GET['site']);
unset($_GET['path']);
unset($_GET['element']);

$cacheKey = 'main_news_'.((int) RhdHandler::isEnglish());
if (!($mainNews = RhdMemcache::get($cacheKey))) {
    $mainSite =
        CIBlock::GetList(
            array(),
            array(
                'CODE' => RhdHandler::isEnglish() ? RhdHandler::getEnglishSiteCode() : RhdHandler::getMainSiteCode()
            )
        )->
        GetNext();
    $mainNewsSection = end(RhdPath::resolvePath($mainSite, 'press/news'));

    $mainNewsRs = CIBlocKElement::GetList(array('ACTIVE_FROM' => 'desc'), array('SECTION_ID' => $mainNewsSection['ID'], 'ACTIVE' => 'Y'), false, array('nTopCount' => 4));
    $mainNews = array();
    while ($news = $mainNewsRs->GetNext()) {
        $iblock = CIBlock::GetByID($news['IBLOCK_ID'])->GetNext();
        $mainNews[] =
            array_merge(
                $news,
                array(
                    'URL' => RhdPath::createUrl($iblock, RhdPath::build($news['IBLOCK_SECTION_ID']), $news['ID'])
                )
            );
    }
    unset($mainNewsRs);

    RhdMemcache::set($cacheKey, $mainNews);
}

$GLOBALS['mainNews'] = $mainNews;

